import React, { useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { Button,Col,Row,Card,CardImg,Container ,Stack} from 'react-bootstrap';

//https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&camera=fhaz&api_key=DEMO_KEY

      //https://api.nasa.gov/planetary/apod?api_key=t6p3bUynsAH2pO5wHnA9hbeZmuT6FUbby03zEzJw
//      "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&camera=fhaz&api_key=t6p3bUynsAH2pO5wHnA9hbeZmuT6FUbby03zEzJw"


const App = () => {
  const [data, setData] = useState([]);


  const fetchData = async () => {
    try{
    const res = await axios.get(
     // "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=t6p3bUynsAH2pO5wHnA9hbeZmuT6FUbby03zEzJw"
"https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&page=2&api_key=t6p3bUynsAH2pO5wHnA9hbeZmuT6FUbby03zEzJw"

    );
    const datas = res.data;

    setData(datas);
    }
    catch(error){
      console.log(error);
    }


  };



  console.log(data);

const x = data.photos

  console.log(x);







  useEffect(() => {
    fetchData();
  }, []);

  const styles = {
    grid: {
        paddingLeft: 0,
        paddingRight: 0
    },
    row: {
        marginBottom: 20,
        marginTop: 20,


        },
    col: {
        marginRight:-20,


    },
    br:{
      borderRadius:20,
      width: '18rem'
    }
    }

    const [isLiked, updateLike] = useState(false);
  const [likedBands, updateLikedBands] = React.useState([]);

  const handleLike = () => {
    let y=x.id
    let currentLikedBands = likedBands;
    if (!isLiked) {
      updateLike(true);
      if (!currentLikedBands.includes(y))
        props.updateLikedBands([...currentLikedBands, y]);
    } else {
      updateLike(false);
      if (currentLikedBands.includes(y))
        props.updateLikedBands(currentLikedBands.filter(band => band !== y));
    }
  };


  return (
    <div>


<h2>spectagram</h2>


{/*       {Object.keys(data).map((item,e) => {   */}


<Container  fluid={true} style={styles.col} >
            <Row   xs={1} sm={2} md={3} lg={4} xl={4}   >

       {x.length > 0 && 

       data.photos.map((item) => (
        <Col style={styles.row}>
          <Card style={styles.br} key={item.id}>
          <Card.Img variant="top" style={{borderRadiusTop:2}} src={item.img_src} height='200' width='200' />
          <Card.Body>
            <Card.Title>{item.camera.full_name}</Card.Title>
            <Card.Text>
              Earth date:{item.earth_date}
            </Card.Text>
            <Card.Text>
              launch date:{item.rover.launch_date}
            </Card.Text>
            <Card.Text>
             landing date: {item.rover.landing_date}
            </Card.Text>
            <Card.Text>
             Rover name: {item.rover.name}
            </Card.Text><Card.Text>
              status:{item.rover.status}
            </Card.Text>
            <Stack direction="horizontal" gap={3}>

  <Button variant="primary" onClick={handleLike} disabled={isLiked}>Like</Button>
  <div className="vr" />
  <Button variant="outline-danger" nClick={handleLike} disabled={!isLiked}>Dislike</Button>
  <p>You {isLiked ? "liked" : "disliked"} </p>
</Stack>
          </Card.Body>
        </Card>
           </Col> 







       ))

       }
       </Row>
       </Container>


    </div>
  );
};

export default App;


